import React, { useRef, useState } from 'react'
import { Link } from 'react-router-dom'
import AdminSidebar from '../AdminSidebar'

export default function CreateMaincategory() {
    let name = useRef("")
    let [message, setMessage] = useState("")
    let [show, setShow] = useState(false)
    function getInputData(e){
        setShow(false)
        name.current = e.target.value
    }
    async function postData(e){
        e.preventDefault()
        if(name.current.length===0){
            setShow(true)
            setMessage("Maincategory Name Must Required")
        }
        else if(name.current.length<3){
            setShow(true)
            setMessage("Maincategory Name Must Be Greater Then or Equal to 3")
        }
        else if(name.current.length>15){
            setShow(true)
            setMessage("Maincategory Name Must Be Less Then or Equal to 15")
        }
        else{
            let response = await fetch("http://localhost:8000/maincategory",{
                method:"GET",
                headers:{
                    "content-type":"application/json"
                }
            })
            response = await response.json()
            var item = response.slice(1).find((x)=>x.name===name.current)
            if(item){
                setShow(true)
                setMessage("Maincategory Alredy Exist")
            }
     else
    alert("Maincategory Is Created!!!")
        }
    }
  return (
    <>
    
      {/* <!-- Single Page Header start --> */}
      <div className="container-fluid page-header carousel-image py-5">
            <h1 className="text-center text-white display-6">Admin</h1>
            <ol className="breadcrumb justify-content-center mb-0">
                <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                
                <li className="breadcrumb-item active text-white">Create Maincategory</li>
            </ol>
        </div>
        {/* <!-- Single Page Header End --> */}
        <div className="container-fluid my-3" >
            <div className="row">
                <div className="col-md-3">
<AdminSidebar/>
                </div>

                <div className="col-md-9">
<div className="row">
  <h5 className='bg-primary text-center text-light p-2 '>Create Maincategory</h5>
<form action="" onSubmit={postData}>
    <div className="mb-3">
        <label htmlFor="">Name<span className='text-danger'>*</span></label>
        <input type="text" name='name' id=''className='form-control' placeholder='Maincategory Name' onChange={getInputData}/>
       {show? <p className='text-danger fs-6'>{message}</p>: ""}
    </div>
    <div className="mb-3">
        <button className='btn btn-primary text-light w-100' type='submit'>Submit</button>
    </div>
</form>
</div>
                </div>
            </div>
        </div>
    </>
  )
}
