import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import AdminSidebar from '../AdminSidebar'

export default function Maincategory() {
  let [data , setData] = useState([])
 async function deleteItem(id){
    let response = await fetch("http://localhost:8000/maincategory/"+id,{
      method: "DELETE",
      headers:{
        "content-type":"application/json"
      }
    })
  }
  useEffect(async()=>{
    let response = await fetch("http://localhost:8000/maincategory", {
      method: "GET",
      headers: {
          "content-type": "application/json"
      }
  })
  response = await response.json()
  if(response)
  setData(response.slice(1))
else
alert("Something Went to Wrong")
},[])
  return (
    <>
    
      {/* <!-- Single Page Header start --> */}
      <div className="container-fluid page-header carousel-image py-5">
            <h1 className="text-center text-white display-6">Admin</h1>
            <ol className="breadcrumb justify-content-center mb-0">
                <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                
                <li className="breadcrumb-item active text-white">Maincategory</li>
            </ol>
        </div>
        {/* <!-- Single Page Header End --> */}
        <div className="container-fluid my-3" >
            <div className="row">
                <div className="col-md-3">
<AdminSidebar/>
                </div>

                <div className="col-md-9">
<div className="row">
  <h5 className='bg-primary text-center text-light p-2 '>Maincategory<Link to="/Admin/Maincategory/Create"><i className='fas fa-plus text-white float-end pe-2'></i></Link></h5>
<table className='table'>
  <thead>
    <tr>
      <th>ID</th>
      <th>Name</th>
      <th></th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    {
      data.map((item,index)=>{
        return <tr key={index}>
            <td>{item.id}</td>
            <td>{item.name}</td>
            <td><Link to={`/admin/maincategory/update/${item.id}`}></Link><i className='fas fa-edit ' style={{color:"#0577fa"}} ></i></td>
            <td><button className='btn text-danger'><i className='fas fa-trash-alt' onClick={()=>deleteItem(item.id)}></i></button></td>
        </tr>
      })
    }
  </tbody>
</table>
</div>
                </div>
            </div>
        </div>
    </>
  )
}
